def isPositive(n: int | float) -> bool:
    """
    Returns whether `n` is positive
    :param n: int | float
    :return: bool
    """
    return n > 0

def isNegative(n: int | float) -> bool:
    """
    Returns whether `n` is negative
    :param n: int | float
    :return: bool
    """
    return n < 0

def isZero(n: int | float) -> bool:
    """
    Returns whether `n` is negative
    :param n: int | float
    :return: bool
    """
    return n == 0
