class Theme:
    DangerBackground = '#c50f1f'
    DangerBackgroundHover = '#b10e1c'
    DangerBackgroundPressed = '#960b18'


class Light(Theme):
    Primary = '#0f6cbd'  # 主要颜色
    PrimaryHover = '#115ea3'
    PrimaryPressed = '#0c3b5e'
    Text = "#242424"  # 文本颜色
    Background = '#fff'  # 背景颜色
    Border = "#d1d1d1"  # 边框颜色


class Dark(Theme):
    Primary = '#115ea3'  # 主要颜色
    Text = "#fff"  # 文本颜色
    Background = '#292929'  # 背景颜色
    Border = "#666666"  # 边框颜色
